import React, { useState } from 'react';
import firebase from '../firebase';
import auth from '../firebase';

const RegisterPage: React.FC = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        const passwordRegex = /^(?=.*[a-z])(?=.*[A-Z]).{8,}$/;

        if (!passwordRegex.test(password)) {
            alert('Password must be at least 8 characters long and must contain both uppercase and lowercase characters.');
            return;
        }

        try {
            await firebase.auth().createUserWithEmailAndPassword(username, password);
            alert("Account created successfully");
        } catch (error) {
            alert(error.message);
        }

        console.log('User registered:', { username, password });
    };

    return (
        <div>
            <h2>Register</h2>
            <p>Password must be at least 8 characters long and must contain both uppercase and lowercase characters.</p>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    placeholder="Username"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
                <button type="submit">Register</button>
            </form>
        </div>
    );
};

export default RegisterPage;
